const n=async o=>{o.preventDefault();const s=document.querySelector("form"),t=new FormData(s),a=await fetch("http://localhost:5000/api/v1/register",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:t.get("username"),password:t.get("password"),email:t.get("email")})});if(a.ok){const e=await a.json();localStorage.setItem("user",JSON.stringify(e[1])),console.log(e),window.location.replace("/")}else{const e=await a.text();alert(e)}};setInterval(()=>{document.getElementById("register").addEventListener("click",n)},3e3);
