const l=async a=>{a.preventDefault();const n=document.querySelector("#login-form"),t=new FormData(n);console.log(t);const s=t.get("password"),i=t.get("email");if(!s||!i){alert("Please fill in form");return}const o=await fetch("http://localhost:5000/api/v1/signin",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({password:t.get("password"),email:t.get("email")})});if(o.ok){const e=await o.json();console.log(e.data),localStorage.setItem("user",JSON.stringify(e.data)),window.location.replace("/")}else{const e=await o.text();alert(e)}};setInterval(()=>{document.getElementById("signIn").addEventListener("click",l)},3e3);
